mapper.insert:

  SELECT LAST_INSERT_ID()

insert into special_course (title, publish_time, expire_time, 
  is_delete, venue_id, intro
  )
values (#{title,jdbcType=BIGINT}, #{publishTime,jdbcType=TIMESTAMP}, #{expireTime,jdbcType=TIMESTAMP}, 
  #{isDelete,jdbcType=TINYINT}, #{venueId,jdbcType=BIGINT}, #{intro,jdbcType=LONGVARCHAR}
  )

mapper.select:
select

,

from special_course
where venue_id = #{venueId,jdbcType=BIGINT}
AND publish_time <= #{date}
AND  expire_time >= #{date}
AND   is_delete = #{isDelete}
ORDER BY publish_time DESC
limit 1
